import React, { useState, useEffect } from 'react';
import Overview from './components/Overview';
import './App.css';

// Load data from JSON or use defaults
const loadData = () => {
  try {
    // In production, this would fetch from a JSON file
    return {
      todayChanges: [
        { id: 1, competitor: 'Crawfort', item: 'homepage', change: '"5 min approval" claim', time: '2h ago', type: 'messaging' },
        { id: 2, competitor: 'Lendela', item: 'campaign', change: 'CNY campaign started', time: '5h ago', type: 'ads' },
        { id: 3, competitor: 'Lending Bee', item: 'blog', change: 'Scam guide published', time: '8h ago', type: 'content' },
      ],
      completed: [
        { competitor: 'Roshi', item: 'cashback update', date: 'Feb 23', time: '2:30 PM' },
      ],
      lastUpdated: new Date().toISOString()
    };
  } catch (e) {
    return { todayChanges: [], completed: [], lastUpdated: new Date().toISOString() };
  }
};

function App() {
  const [data, setData] = useState(loadData());
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 60000);
    return () => clearInterval(timer);
  }, []);

  const formatTime = (date) => {
    return date.toLocaleString('en-SG', { 
      timeZone: 'Asia/Singapore',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="app">
      <header className="header">
        <div className="logo">
          <span className="logo-icon">ðŸ“Š</span>
          <h1>PMAL Competitor Tracker</h1>
        </div>
        <span className="last-updated">{formatTime(currentTime)} SGT</span>
      </header>

      <main className="main">
        <Overview data={data} />
      </main>

      <footer className="footer">
        <p>Click âœ… Done to send update to Telegram Â· Mobile optimized</p>
      </footer>
    </div>
  );
}

export default App;
