import React, { useState } from 'react';

function Overview({ data }) {
  const [expanded, setExpanded] = useState({});

  const toggleExpand = (id) => {
    setExpanded(prev => ({ ...prev, [id]: !prev[id] }));
  };

  const generateTelegramLink = (action, competitor, item, date) => {
    const message = `/${action} ${competitor} ${item} ${date}`;
    return `https://t.me/Clementteojr?text=${encodeURIComponent(message)}`;
  };

  const todayChanges = data.todayChanges || [
    { id: 1, competitor: 'Crawfort', item: 'homepage', change: '"5 min approval" claim', time: '2h ago', type: 'messaging' },
    { id: 2, competitor: 'Lendela', item: 'campaign', change: 'CNY campaign started', time: '5h ago', type: 'ads' },
    { id: 3, competitor: 'Lending Bee', item: 'blog', change: 'Scam guide published', time: '8h ago', type: 'content' },
  ];

  const completed = data.completed || [
    { competitor: 'Roshi', item: 'cashback update', date: 'Feb 23', time: '2:30 PM' },
  ];

  const suggestedActions = [
    { text: 'Review Crawfort speed claim vs your "instant" positioning', priority: 'high' },
    { text: 'Check Lendela CNY campaign creative', priority: 'medium' },
    { text: 'Read Lending Bee scam guide for content ideas', priority: 'low' },
  ];

  const getTypeIcon = (type) => {
    switch (type) {
      case 'messaging': return 'üí¨';
      case 'ads': return 'üì¢';
      case 'content': return 'üìù';
      default: return 'üîÑ';
    }
  };

  return (
    <div className="overview">
      <div className="overview-header">
        <h2>Today</h2>
        <span className="change-count">{todayChanges.length} changes ¬∑ {completed.length} completed</span>
      </div>

      <div className="changes-list">
        {todayChanges.map(change => (
          <div key={change.id} className="change-card">
            <div className="change-header" onClick={() => toggleExpand(change.id)}>
              <span className="change-icon">{getTypeIcon(change.type)}</span>
              <div className="change-title">
                <strong>{change.competitor}</strong>
                <span className="change-desc">{change.change}</span>
              </div>
              <span className="change-time">{change.time}</span>
              <span className="expand-icon">{expanded[change.id] ? '‚ñº' : '‚ñ∂'}</span>
            </div>

            {expanded[change.id] && (
              <div className="change-actions">
                <a 
                  href={generateTelegramLink('done', change.competitor.toLowerCase(), change.item, new Date().toISOString().split('T')[0])}
                  className="action-btn done"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  ‚úÖ Done
                </a>
                <a 
                  href={generateTelegramLink('check', change.competitor.toLowerCase(), change.item, new Date().toISOString().split('T')[0])}
                  className="action-btn check"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  üîç Check
                </a>
                <a 
                  href={generateTelegramLink('note', change.competitor.toLowerCase(), change.item, new Date().toISOString().split('T')[0])}
                  className="action-btn note"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  üìù Note
                </a>
              </div>
            )}
          </div>
        ))}
      </div>

      {suggestedActions.length > 0 && (
        <div className="suggested-actions">
          <h3>üí° Suggested Actions</h3>
          <ul>
            {suggestedActions.map((action, idx) => (
              <li key={idx} className={`priority-${action.priority}`}>
                <span className="priority-dot"></span>
                {action.text}
              </li>
            ))}
          </ul>
        </div>
      )}

      {completed.length > 0 && (
        <div className="completed-section">
          <h3>‚úì Completed</h3>
          <ul className="completed-list">
            {completed.map((item, idx) => (
              <li key={idx} className="completed-item">
                <span className="completed-text">
                  ~~{item.competitor}: {item.item}~~
                </span>
                <span className="completed-meta">
                  ‚úì {item.date}, {item.time}
                </span>
              </li>
            ))}
          </ul>
        </div>
      )}

      <div className="this-week">
        <h3>This Week</h3>
        <div className="week-list">
          <div className="week-item"><span className="week-day">Mon</span> Crawfort site update</div>
          <div className="week-item"><span className="week-day">Sun</span> Lendela ads</div>
          <div className="week-item"><span className="week-day">Fri</span> Lending Bee blog</div>
        </div>
        <a href="#history" className="view-all">View full history ‚Üí</a>
      </div>
    </div>
  );
}

export default Overview;
